        [TestMethod()]
        public void GetDateTest()
        {
            try
            {
                var obj = new SharpObject();
                obj["dtm"] = DateTime.Now;
                var dtm = obj.GetDate("dtm");
                Assert.IsNotNull(dtm);
            }
            catch (Exception ex)
            {
                Assert.Fail(ex.Message);
                throw;
            }
        }

